try {
  if (msg.payload.allSites.edges.length > 0) {
    msg = {
      options: msg.payload.allSites.edges.map((edge) => ({
        [edge.node.name]: edge.node.id,
      })),
    };
  } else {
    let empty_result = "No sites found";
    msg = {
      options: [empty_result],
      payload: empty_result,
    };
  }
} catch (error) {
  let error_msg = "Error requesting sites";
  msg = {
    options: [error_msg],
    payload: error_msg,
  };
}
return msg;

try {
  if (msg.payload.allSiteEntities.edges.length > 0) {
    msg = {
      options: msg.payload.allSiteEntities.edges.map((edge) => ({
        [edge.node.name]: edge.node.id,
      })),
    };
  } else {
    let empty_result = "No controllers found";
    msg = {
      options: [empty_result],
      payload: empty_result,
    };
  }
} catch (error) {
  let error_msg = "Error requesting controllers";
  msg = {
    options: [error_msg],
    payload: error_msg,
  };
}

return msg;

let have_controllers = false;
try{
    have_controllers = Boolean(
        msg.payload.allSiteEntities.edges
    );
} catch (error) {}



msg.payload = {
    group: {
        show: [],
        hide: []
    }
};
if (have_controllers) {
    msg.payload.group.show.push("Home_Controllers");
} else {
    msg.payload.group.hide.push("Home_Controllers");
}
msg.payload.group.hide.push("Home_PeripheralData");
msg.payload.group.hide.push("Home_ControllerDetails");

return msg;

let controller_msg = { payload: msg.payload.siteEntity.name };

let peripherals_msg = {};
try {
  edges =
    msg.payload.siteEntity.controllerComponent.peripheralComponentSet.edges;
  if (edges.length > 0) {
    peripherals_msg.options = edges.map((edge) => ({
      [edge.node.siteEntity.name]: edge.node.siteEntity.id,
    }));
    peripherals_msg.peripherals = peripherals_msg["options"];
  } else {
    let empty_results = "No peripherals found";
    peripherals_msg.options = [empty_results];
    peripherals_msg.payload = empty_results;
    peripherals_msg.peripherals = [];
  }
} catch (error) {
  let error_msg = "Error requesting peripherals";
  peripherals_msg.options = [error_msg];
  peripherals_msg.payload = error_msg;
  peripherals_msg.peripherals = [];
}

let start_task_msg = { enabled: false };

return [controller_msg, peripherals_msg, start_task_msg];

