);
return {options: options};



let values = msg.payload.controllerTaskEnums.taskTypeValues;
let labels = msg.payload.controllerTaskEnums.taskTypeLabels;

for(let i = 0; i < values.length; i++) {
    let option = {};
    option[labels[i]] = values[i];
    options.push(option);
}
return {"options": options};

if (msg.peripherals) {
  // peripherals has the format [{peripheralName: peripheralGID}]
  context.set("peripherals", msg.peripherals);
} else if (msg.topic === "startTask") {
  let peripherals = context.get("peripherals");
  let selectedPeripheral = context.get("peripheral");
  let peripheral = peripherals.find(
    (peripheral) => Object.values(peripheral)[0] === selectedPeripheral
  );
  msg.payload = {
      peripheralGID: Object.values(peripheral)[0],
      peripheralName: Object.keys(peripheral)[0],
      controllerName: flow.get("controllerName")
  };
  return msg;
} else {
  // peripheral has the format {peripheralName: peripheralGID}
  context.set("peripheral", msg.payload);
  return null;
}

// Store peripheral as {peripheralName: "some name", peripheralGID: "peripheral GID"}
flow.set("peripheral", msg.payload)

return {payload: msg.payload.peripheralName};

let taskTypes = flow.get("taskTypes");
hideGroups = taskTypes
  .filter(taskType => taskType.value != msg.payload)
  .map((taskType) => "StartTask_".concat(taskType.value));
showGroup = "StartTask_".concat(msg.payload);

return {payload: {group: {show: showGroup , hide: hideGroups}}};

parameters = {};
// Set the peripheral UUID
const peripheralGID = new Buffer(
  flow.get("peripheral").peripheralGID,
  "base64"
);
parameters["peripheral"] = peripheralGID.toString().split(":")[1];
// Set the data point type UUID
const dataPointTypeGID = new Buffer.from(
  flow.get("writeActuatorDataPointTypeGID"),
  "base64"
).toString();
parameters["data_point_type"] = dataPointTypeGID.split(":")[1];
// Set the value
parameters["value"] = msg.payload.value;

return {
  variables: {
    input: {
      controllerComponent: flow.get("controllerComponentGID"),
      taskType: flow.get("taskType"),
      parameters: JSON.stringify(parameters),
    },
  },
};

try {
    flow.set("controllerComponentGID", msg.payload.siteEntity.peripheralComponent.controllerComponent.id);
